package ch.cyberduck.core.sftp;

import ch.cyberduck.core.Host;
import ch.cyberduck.core.preferences.PreferencesFactory;

import org.junit.Test;

import java.math.BigInteger;
import java.security.PublicKey;
import java.security.spec.RSAPublicKeySpec;

import net.schmizz.sshj.common.SecurityUtils;

import static org.junit.Assert.*;

public class PreferencesHostKeyVerifierTest {

    @Test
    public void testVerifyAcceptServerHostKey() throws Exception {
        PreferencesHostKeyVerifier v = new PreferencesHostKeyVerifier() {
            @Override
            public boolean isChangedKeyAccepted(Host hostname, PublicKey key) {
                return false;
            }

            @Override
            public boolean isUnknownKeyAccepted(Host hostname, final PublicKey key) {
                return true;
            }
        };
        final PublicKey key = SecurityUtils.getKeyFactory("RSA").generatePublic(new RSAPublicKeySpec(new BigInteger("", 16),
            new BigInteger("23", 16)));
        assertTrue(v.verify(new Host(new SFTPProtocol(), "ahostname", 22), key));
        assertNull(PreferencesFactory.get().getProperty("ssh.hostkey.ssh-rsa.ahostname"));
        v.allow(new Host(new SFTPProtocol(), "ahostname", 22), key, false);
        assertNull(PreferencesFactory.get().getProperty("ssh.hostkey.ssh-rsa.ahostname"));
        v.allow(new Host(new SFTPProtocol(), "ahostname", 22), key, true);
        assertNotNull(PreferencesFactory.get().getProperty("ssh.hostkey.ssh-rsa.ahostname"));
        assertEquals("",
            PreferencesFactory.get().getProperty("ssh.hostkey.ssh-rsa.ahostname"));
    }

    @Test
    public void testVerifyDenyServerHostKey() throws Exception {
        PreferencesHostKeyVerifier v = new PreferencesHostKeyVerifier() {
            @Override
            public boolean isChangedKeyAccepted(Host hostname, PublicKey key) {
                return false;
            }

            @Override
            public boolean isUnknownKeyAccepted(Host hostname, final PublicKey key) {
                return false;
            }
        };
        final PublicKey key = SecurityUtils.getKeyFactory("RSA").generatePublic(new RSAPublicKeySpec(new BigInteger("", 16),
            new BigInteger("23", 16)));
        assertFalse(v.verify(new Host(new SFTPProtocol(), "bhostname", 22), key));
        assertNull(PreferencesFactory.get().getProperty("ssh.hostkey.ssh-rsa.bhostname"));
    }
}
